{
  "devices": [
    {
      "device_id": "lamp001",         // Identificador único do dispositivo
      "entity_name": "urn:ngsi-ld:Lamp:001",   // Nome da entidade para o dispositivo (formato URN)
      "entity_type": "Lamp",          // Tipo da entidade (neste caso, "Lamp")
      "protocol": "PDI-IoTA-UltraLight",  // Protocolo usado para a comunicação (no exemplo, PDI-IoTA-UltraLight)
      "transport": "MQTT",            // Método de transporte (neste caso, MQTT)

      // Lista de comandos que o dispositivo pode receber
      "commands": [
        { "name": "on", "type": "command" },  // Comando para ligar
        { "name": "off", "type": "command" }  // Comando para desligar
      ],

      // Lista de atributos associados ao dispositivo
      "attributes": [
        { "object_id": "s", "name": "state", "type": "Text" }, // Atributo de estado (Texto)
        { "object_id": "l", "name": "luminosity", "type": "Integer" }  // Atributo de luminosidade (Inteiro)
      ]
    }
  ]
}

{
  "description": "Lamp Commands", // Descrição do serviço de notificação
  "dataProvided": {
    "entities": [
      {
        "id": "urn:ngsi-ld:Lamp:001", "type": "Lamp" // Definição da entidade a ser monitorada
      }
    ],
    "attrs": ["on", "off"] // Atributos a serem monitorados (no caso, 'on' e 'off')
  },
  "provider": {
    "http": { "url": "http://{{url}}:4041" }, // URL do serviço a ser notificado
    "legacyForwarding": true // Indicação para encaminhar notificações de forma legada (se aplicável)
  }
}

{
  "description": "Notify STH-Comet of all Motion Sensor count changes", // Descrição da notificação
  "subject": {
    "entities": [
      {
        "id": "urn:ngsi-ld:Lamp:001",
        "type": "Lamp"
      }
    ],
    "condition": { "attrs": ["luminosity"] } // Condição para acionar a notificação (alteração de luminosidade)
  },
  "notification": {
    "http": {
      "url": "http://{{url}}:8666/notify" // URL para notificação do STH-Comet
    },
    "attrs": [
      "luminosity" // Atributo a ser notificado (luminosidade)
    ],
    "attrsFormat": "legacy" // Formato dos atributos a ser notificado (legado)
  }
}

{
  "description": "Notify STH-Comet of all Env001 noiseLevel changes",
